COMMAND.ACTION:
. /etc/default/ngcp-roles
case "${host}" in "all") parallel-ssh -t 2 -O "BatchMode=yes" -H "$NGCP_HOSTS" -i "cat /etc/motd ; monit summary" ;;
"active") parallel-ssh -t 2 -O "BatchMode=yes" -H "$NGCP_HOSTS" -i \ "cat /etc/motd ; if /usr/sbin/ngcp-check_active -q ; then monit summary ; fi" ;;
"inactive") parallel-ssh -t 5 -O "BatchMode=yes" -H "$NGCP_HOSTS" -i \ "cat /etc/motd ; if ! /usr/sbin/ngcp-check_active -q ; then monit summary ; fi" ;;
*) parallel-ssh -t 5 -O "BatchMode=yes" -H "${host}" -i "cat /etc/motd ; monit summary" ;; esac

. /etc/default/ngcp-roles
case "${host}" in "all") parallel-ssh -t 5 -O "BatchMode=yes" -H "$NGCP_HOSTS" -i \ "cat /etc/motd | egrep -v '^$' ; echo -n 'Heartbeat reports status: ' ; cl_status rscstatus ;" ;;
*) parallel-ssh -t 5 -O "BatchMode=yes" -H "${host}" -i \ "cat /etc/motd | egrep -v '^$' ; echo -n 'Heartbeat reports status: ' ; cl_status rscstatus ;" ;; esac
